import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, TextInput, Image} from 'react-native';
//import {selectedNumber} from './HaveCRScreen';

const OTPScreen = ({navigation}) => {

  const mobileNumber = navigation.getParam('paramKey');
  const patientName = navigation.getParam('patientKey');
  const crno = navigation.getParam('crKey'); 
  const systemMobileNumber = navigation.getParam('mobileKey');
  const patientHasWallet = navigation.getParam('walletKey');
  const OTP = navigation.getParam('otpKey');

  // console.log(mobileNumber);
  // console.log(patientName);
  console.log(OTP);

  //last for digits of the mobileNumber
  var lastFourDigits = mobileNumber.substring(6);

  const [enteredValue,setenteredValue] =useState('');
  const [selectedOTP,setselectedOTP] = useState('');

  const numberInputHandler = inputText => {
    setenteredValue(inputText);
  };

  const confirmInputHandler = () => 
  {
    const choosenNumber = enteredValue;
    setselectedOTP(choosenNumber);
        setenteredValue('');
  };


  //OTP Check
  const otpAuth = () => {
    if(selectedOTP != OTP) {
     alert("Wrong OTP, please try again.");  
    } else {
         {navigation.navigate('Home', {
            paramKey: mobileNumber,
            patKey: patientName,
            crnoKey: crno,
          })
        }}
    }


      return (
        <View style={styles.container}>
        <StatusBar style="auto" backgroundColor= "#368cb3"  />
        <Text style={{fontSize: 20, marginTop: 8 , color: '#427f95' }}>Nizam's Institute of Medical Sciences</Text>
        <Text style={{fontSize: 13, color: '#427f95'  }}>(A University Established Under State Act)</Text>
        <Text style={{fontSize: 17, color: '#427f95'  }}>Hyderbad 500082 Telangana, India</Text>
        <Image style={{height: 160, width: 140, margin: 20, marginBottom: 55}} source={require('../../assets/nimslogo_liteblue.png')} />

      <Text style={{ fontSize: 15, color: "#427f95", paddingLeft: 15,}}>Please enter the 6-digit One-Time Password(OTP) received on the registered mobile number ending with XXXXXX{lastFourDigits}</Text>

      <TextInput  
      style={{height: 40,
      fontSize: 20, 
      width: 260,
      borderColor:'#368cb3',
      borderBottomWidth: 2,
      paddingLeft: 7,
      margin: 20}}
      maxLength= {6}
      onChangeText={numberInputHandler}
      value={enteredValue}
      onSubmitEditing = {()=> confirmInputHandler()}
      placeholder="Enter OTP"/>


      <TouchableOpacity style={styles.appButtonContainer}
      //  onPress={() => { 
      //    props.navigation.navigate({routeName: 'Home'},
      //    {value: num})
      //    ;}}>
      onPress={() => {
        otpAuth();
      }
      }


      //onPress={() => { props.navigation.navigate({routeName: 'Home'}); }}
       >

        <Text style={styles.appButtonText}>Login</Text>
      </TouchableOpacity>

      <Image style={{height: 70, width: 180, marginTop: 40}} source={require('../../assets/toolbarlogo1.png')} />

    </View>
        )
   }


export default OTPScreen;

const styles = StyleSheet.create({
  container: {
    flexDirection: 'column',
    backgroundColor: '#eef2f7',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 80,
  },
  appButtonContainer: {
    marginTop: 8,
    backgroundColor: "#368cb3",
    borderRadius: 25,
    width: 180,
    paddingVertical: 10,
    paddingHorizontal: 12
  },
  appButtonText: {
    fontSize: 17,
    color: "black",
    fontWeight: "bold",
    alignSelf: "center",
    textTransform: "uppercase"
  },

});
