import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, TextInput, Image, ActivityIndicator} from 'react-native';

//link for otp = https://nimsts.edu.in/HISServices/service/login/otp?hCode=33101&mobileNo=7988185383


const HaveCRScreen = ({navigation}) => {


  const URL = "https://run.mocky.io/v3/36c78c3d-c193-4d91-b9e7-4b2a476260b8";

  const [isLoading, setLoading] = useState(true);
  const [otpn, setotpn] = useState();
  const [patientName, setpatientName] = useState();
  const [crno, setcrno] = useState();
  const [mobileNo, setmobileNo] = useState();
  const [patientHasWallet, setpatientHasWallet] = useState();



  // console.log(patient);

  // var otpn = data; 
  // var patientName = patient[0].patName
  // var crno = patient[0].crno
  // var mobileNo = patient[0].mobileNo
  // var patientHasWallet = patient[0].patientHasWallet


  let content 
  let jsonDATA

  useEffect(() => {
    (async () => {
    content = await fetch(URL)
    jsonDATA = await content.json()
    console.log(jsonDATA);
    setotpn(jsonDATA.OTP)
    setpatientName(jsonDATA.patientdetails[0].patName)
    setcrno(jsonDATA.patientdetails[0].crno)
    setmobileNo(jsonDATA.patientdetails[0].mobileNo)
    setpatientHasWallet(jsonDATA.patientdetails[0].patientHasWallet)
    setLoading(false);
    }
    )();
  }, []);

    const [enteredValue,setenteredValue] =useState('');
    const [selectedNumber,setselectedNumber] = useState('');

    const numberInputHandler = inputText => {
      setenteredValue(inputText);
    };

    // let output;
    // let ischeck;
    let Number;

    const confirmInputHandler = () => 
    {
      const choosenNumber = enteredValue;
      setselectedNumber(choosenNumber);
          setenteredValue('');
    };

    // if(selectedNumber !== null)
    // {
    // }
    // let operate = confirmInputHandler();
    //Number Check
     const userAuth = () => {
     if(selectedNumber != mobileNo) {
      alert("Not an User!");  
     } else {
          {navigation.navigate('OTP', {
          paramKey: selectedNumber,
          otpKey: otpn,
          patientKey: patientName,
          crKey: crno,
          mobileKey: mobileNo,
          walletKey: patientHasWallet,
        })}
     }
    }

    return (
      <View style={styles.container}>
      <StatusBar style="auto" backgroundColor= "#368cb3"  />

      {isLoading ? (
         <View>
           <ActivityIndicator 
        color = '#368cb3'
        size = "large"
        style = {styles.activityIndicator}
           />

         </View>
        ) : (
          <>




      <Text style={{fontSize: 20, marginTop: 20, color: '#427f95' }}>Nizam's Institute of Medical Sciences</Text>
      <Text style={{fontSize: 13, color: '#427f95'  }}>(A University Established Under State Act)</Text>
      <Text style={{fontSize: 17, color: '#427f95'  }}>Hyderbad 500082 Telangana, India</Text>
      <Image style={{height: 160, width: 140, margin: 30,}} source={require('../../assets/nimslogo_liteblue.png')} />

      <Text style={{ fontSize: 20, color: "black", alignSelf: "center",}}>Enter Mobile No.</Text>

      <TextInput  
      style={{height: 40,
      backgroundColor: 'white', 
      fontSize: 20, width: 260,
      borderRadius: 10,
      borderColor:'#368cb3',
      borderWidth: 2,
      paddingLeft: 10,
      margin: 20,}} 
      maxLength= {10}
      onChangeText={numberInputHandler}
      value={enteredValue}
      onSubmitEditing = {()=> confirmInputHandler()}
      placeholder="Mobile No"/>

      <TouchableOpacity style={styles.appButtonContainer} 
      onPress={() =>
        {navigation.navigate('OTP', {
          paramKey: selectedNumber,
          patientKey: patientName,
          crKey: crno,
          mobileKey: mobileNo,
          walletKey: patientHasWallet,
        })}
      }
      onPressIn={() => {
        userAuth();
      }
      }
      >
      <Text style={styles.appButtonText}>Get OTP</Text>
      </TouchableOpacity>
      <Image style={{height: 70, width: 180, marginTop:30}} source={require('../../assets/toolbarlogo1.png')} />




      </>
        )}





    </View>
        )
    } 
export default HaveCRScreen;

const styles = StyleSheet.create({
  container: {
    flexDirection: 'column',
    backgroundColor: '#eef2f7',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 80,
  },
  appButtonContainer: {
    marginTop: 8,
    backgroundColor: "#368cb3",
    borderRadius: 25,
    width: 140,
    paddingVertical: 10,
    paddingHorizontal: 12
  },
  appButtonText: {
    fontSize: 15,
    color: "#fff",
    fontWeight: "bold",
    alignSelf: "center",
    textTransform: "uppercase"
  },
  activityIndicator: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    height: 80
  }
});
