import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, TextInput, Image, ActivityIndicator} from 'react-native';

const DontHaveCRScreen = ({navigation}) => {

    //Input for Name
    const [enteredName,setenteredName] =useState('');
    const [selectedName,setselectedName] = useState('');
    const nameInputHandler = inputName => {
      setenteredName(inputName);
    };

    //Input for Mobile no.
    const [enteredNumber,setenteredNumber] =useState('');
    const [selectedNumber,setselectedNumber] = useState('');
    const numberInputHandler = inputText => {
      setenteredNumber(inputText);
    };
    const confirmInputHandler = () => 
    {
      const choosenNumber = enteredNumber;
      setselectedNumber(choosenNumber); 
          //setenteredNumber('');
      const choosenName = enteredName;
      setselectedName(choosenName);
          //setenteredName('');
    };

    console.log(selectedName);
    console.log(selectedNumber);

        return (
        <View style={styles.container}>
        <StatusBar style="auto" backgroundColor= "#368cb3"  />


    {/* for progress icon */}
    {/* <View style={[styles.horizontal]}>
      <ActivityIndicator size="large" color="#368cb3" />
    </View> */}



      <Text style={{fontSize: 20, marginTop: 20 , color: '#427f95' }}>Nizam's Institute of Medical Sciences</Text>
      <Text style={{fontSize: 13, color: '#427f95'  }}>(A University Established Under State Act)</Text>
      <Text style={{fontSize: 17, color: '#427f95'  }}>Hyderbad 500082 Telangana, India</Text>
      <Image style={{height: 160, width: 140, margin: 20,}} source={require('../../assets/nimslogo_liteblue.png')} />

      <Text style={{ fontSize: 22, color: "black", alignSelf: "center",}}>Enter Mobile No.</Text>

      <TextInput  
      style={{height: 40,
      backgroundColor: 'white', 
      fontSize: 20, width: 260,
      borderRadius: 10,
      borderColor:'#368cb3',
      borderWidth: 2,
      paddingLeft: 10,
      margin: 20}} 
      onChangeText={nameInputHandler}
      value={enteredName}
      placeholder="Name"/>

      <TextInput  
      style={{height: 40,
      backgroundColor: 'white', 
      fontSize: 20, width: 260,
      borderRadius: 10,
      borderColor:'#368cb3',
      borderWidth: 2,
      paddingLeft: 10,
      margin: 5}} 
      onChangeText={numberInputHandler}
      value={enteredNumber}
      placeholder="Mobile No"/>


      <TouchableOpacity style={styles.appButtonContainer}
      onPress={() => { 
        navigation.navigate('DontHaveCRHome', {
          nameKey: selectedName,
          numberKey: selectedNumber,
        }); }} 
        onPressIn={() => {  confirmInputHandler(); } }>
        <Text style={styles.appButtonText}>Sign Up</Text>
        </TouchableOpacity>

      <Image style={{height: 70, width: 180, marginTop: 40}} source={require('../../assets/toolbarlogo1.png')} />

    </View>
        )
    }

export default DontHaveCRScreen;

const styles = StyleSheet.create({
  container: {
    flexDirection: 'column',
    backgroundColor: '#eef2f7',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 80,
  },
  appButtonContainer: {
    marginTop: 15,
    backgroundColor: "#368cb3",
    borderRadius: 25,
    width: 140,
    paddingVertical: 10,
    paddingHorizontal: 12
  },
  appButtonText: {
    fontSize: 15,
    color: "#fff",
    fontWeight: "bold",
    alignSelf: "center",
    textTransform: "uppercase"
  },

  horizontal: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 10,
    },

});
